public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String studentnames[]=new String[4];
		studentnames[0]="Ethan";
		studentnames[1]="Aleah";
		studentnames[2]="Amaya";
		studentnames[3]="Nathan";
		System.out.println("Student 1 is " + studentnames[0]);
		System.out.println("Student 2 is " + studentnames[1]);
		System.out.println("Student 3 is " + studentnames[2]);
		System.out.println("Student 4 is " + studentnames[3]);

	}

}
