public class ConstantsExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declare Variables
		final double PI=3.14;
		double radius=5;
		double area=0;
		
		//Process Area
		area=PI * radius * radius;
		
		//Output
		System.out.println("Area of circle: " + area);

	}

}
