public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numbers[]=new int [5];
		numbers[0]=12;
		numbers[1]=23;
		numbers[2]=10;
		numbers[3]=15;
		numbers[4]=9;
		System.out.println("Index 0 in the array holds " + numbers[0]);
		System.out.println("Index 1 in the array holds " + numbers[1]);
		System.out.println("Index 2 in the array holds " + numbers[2]);
		System.out.println("Index 3 in the array holds " + numbers[3]);
		System.out.println("Index 4 in the array holds " + numbers[4]);
	}

}
