public class LoopsExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter=1;
		while (counter<=10)
		{
			System.out.println("Looping in Java displayed " + counter + " time(s).");
			counter=counter+1;
		}


	}

}
